package com.hughes.exercises.Assignments;
/*
 add values from 239 till 699 using for loop.

*/

/**
 * @author KIIT
 *
 */
public class D2q67 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 239;
		int b = 699;
		int sum = 0;
		for(int i = a;i<=b;i++) {
			sum+= i;
		}
		System.out.println(sum);
	}

}
